    hover: {
      onHover: function(e) {
        var point = this.getElementAtEvent(e);
        if (point.length) e.target.style.cursor = 'pointer';
        else e.target.style.cursor = 'default';
      },
      mode: 'nearest',
      intersect: true
    },
    elements: {
      arc: {
        angle: angles
      }
    },

    responsive: true,
    legend: {
      onClick: null,
      display: true,
      position: 'left',
      align: 'center',
      labels: {
        fontSize: 16,
        generateLabels: function(chart) {
          var data = chart.data;
          if (data.labels.length && data.datasets.length) {
            return data.labels.map(function(label, i) {
              var meta = chart.getDatasetMeta(0);
              var ds = data.datasets[0];
              var arc = meta.data[i];
              var custom = (arc && arc.custom) || {};
              var getValueAtIndexOrDefault =
                Chart.helpers.getValueAtIndexOrDefault;
              var arcOpts = chart.options.elements.arc;
              var fill = custom.backgroundColor
                ? custom.backgroundColor
                : getValueAtIndexOrDefault(
                    ds.backgroundColor,
                    i,
                    arcOpts.backgroundColor
                  );
              var stroke = custom.borderColor
                ? custom.borderColor
                : getValueAtIndexOrDefault(
                    ds.borderColor,
                    i,
                    arcOpts.borderColor
                  );
              var bw = custom.borderWidth
                ? custom.borderWidth
                : getValueAtIndexOrDefault(
                    ds.borderWidth,
                    i,
                    arcOpts.borderWidth
                  );

              // We get the value of the current label
              var value =
                chart.config.data.datasets[arc._datasetIndex].data[arc._index];

                var stdvalue = [<?php echo $std_hill_gs; ?>,
         <?php echo $std_hill_mo; ?>,
         <?php echo $std_hill_tm; ?>, 
         <?php echo $std_hill_sm; ?>, 
         <?php echo $std_hill_tem; ?>, 
         <?php echo $std_hill_ii; ?>, 
         <?php echo $std_hill_pi; ?>, 
         <?php echo $std_hill_er; ?>, 
         <?php echo $std_hill_mst; ?>]

              return {
                // Instead of `text: label,`
                // We add the value to the string
                text: label + ' : ' + value + ' (SD = ' + stdvalue[i] + ')',
                fillStyle: fill,
                strokeStyle: stroke,
                lineWidth: bw,
                hidden: isNaN(ds.data[i]) || meta.data[i].hidden,
                index: i
              };
            });
          } else {
            return [];
          }
        }
      }
    },
    title: {
      fontSize:20,
